sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();
    var rightNowPlus = new GlideDateTime();
    rightNowPlus.addDaysLocalTime(30);

    gs.info("rightNow: " + rightNow);
    gs.info("rightNowPlus: " + rightNowPlus);

	// Query the database for NeedIt Task records with Due date field values older
    // with Due dates between now and 24 hours from now
	// The NeedIt Tasks should not have a State field value of Closed Complete
	var dueSoonTasks = new GlideRecord('x_58872_needit_needit_task');
	dueSoonTasks.addQuery('due_date','>=',rightNow);
    dueSoonTasks.addQuery('due_date','<=',rightNowPlus);
    dueSoonTasks.addQuery('state','!=',3);

	dueSoonTasks.query();
	// Write a log message for each overdue NeedIt Task Record
	while(dueSoonTasks.next()){
		gs.info("Due soon NeedIt Task record = " + dueSoonTasks.number);
gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',dueSoonTasks,dueSoonTasks.number,gs.getUserName());
	}
